const initialState = {
    validando: true,
    token: null,
    username: '',
    nombre: ''
}

interface AuthState {
    validando: boolean,
    token: null,
    username: string,
    nombre: string
}

type AuthAction = {type: 'logout'}

const authReducer = (state: AuthState, action: AuthAction): AuthState => {

    switch (action.type) {
        case 'logout':
            return {
                validando: false,
                token: null,
                nombre:'',
