import { useState } from "react";
import { useCounter } from '../hooks/useCounter';

export const ContadorConHook = () => {
    const { valor, acumular } = useCounter();

    return (
        <div>
            <h3>Contador con hook: <small>{valor}</small></h3>

            <button
                className='btn btn-primary'
                onClick={() => acumular(1)}
            >
                +1
           </button>
           &nbsp;
            <button
                className='btn btn-primary'
                onClick={() => acumular(-1)}
            >
                -1
           </button>
        </div>
    )
}
